# coding=utf-8
import binascii


# x=2,y=10,2进制转10进制，n为原始数
def zhuan_jing_zhi(x, y, n):
    if x == 2 and y == 8:
        # 2进制转换成8进制：
        return oct(int(n, 2))
    if x == 2 and y == 10:
        # 2进制转换成10进制：
        return int(n, 2)
    if x == 2 and y == 16:
        # 2进制转换成16进制：
        return hex(int(n, 2))
    if x == 8 and y == 2:
        # 8进制转换成2进制：
        return bin(int(n, 8))
    if x == 8 and y == 10:
        # 8进制转换成10进制：
        return int(n, 8)
    if x == 8 and y == 16:
        # 8进制转换成16进制：
        return hex(int(n, 16))
    if x == 10 and y == 2:
        # 10进制转换成2进制：
        return bin(n)
    if x == 10 and y == 8:
        # 10进制转换成8进制：
        return oct(n)
    if x == 10 and y == 16:
        # 10进制转换成16进制：
        return hex(n)
    if x == 16 and y == 2:
        # 16进制转换成2进制：
        return bin(int(n, 16))
    if x == 16 and y == 8:
        # 16进制转换成8进制：
        return oct(int(n, 16))
    if x == 16 and y == 10:
        # 16进制转换成10进制：
        return int(n, 16)
    print('不能转换')
    return -1


# 16进制转字符
def jing_zhi_zhuan_zhi_fu(n):
    n = str(n).lstrip("0x")
    return_string = binascii.unhexlify(n)
    # print(return_string)
    return return_string

