# coding=utf-8
import binascii


def binary_conversion(x, y, n):
    conversions = {
        (2, 8): lambda: oct(int(n, 2)),  # 2进制转换成8进制
        (2, 10): lambda: int(n, 2),  # 2进制转换成10进制
        (2, 16): lambda: hex(int(n, 2)),  # 2进制转换成16进制
        (8, 2): lambda: bin(int(n, 8)),  # 8进制转换成2进制
        (8, 10): lambda: int(n, 8),  # 8进制转换成10进制
        (8, 16): lambda: hex(int(n, 8)),  # 8进制转换成16进制
        (10, 2): lambda: bin(n),  # 10进制转换成2进制
        (10, 8): lambda: oct(n),  # 10进制转换成8进制
        (10, 16): lambda: hex(n),  # 10进制转换成16进制
        (16, 2): lambda: bin(int(n, 16)),  # 16进制转换成2进制
        (16, 8): lambda: oct(int(n, 16)),  # 16进制转换成8进制
        (16, 10): lambda: int(n, 16),  # 16进制转换成10进制
    }

    if (x, y) in conversions:
        return conversions[(x, y)]()
    else:
        print("不能转换")
        return -1


# 16进制转字符
def hex_to_str(n):
    n = str(n).lstrip("0x")
    return_string = binascii.unhexlify(n)
    # print(return_string)
    return return_string


